                            // <Typography variant="h3" align="center" style={{ position: "relative" }}>
                            //     {item.icon}
                            //     {item.title}
                            // </Typography>
                            
                            // <Box display="flex" align="center">
                            //     {item.icon}
                            //     <Typography variant="h3">
                            //         {item.title}
                            //     </Typography>
                            // </Box>

                            // <List dense={true}>

                            //     {item.info.map((item, index) => (
                            //         <ListItem key={index}>

                            //             <ListItemIcon>
                            //                 <FiberManualRecordIcon />
                            //             </ListItemIcon>

                            //             <ListItemText 
                            //             primary={
                            //             <Typography variant="body1">
                            //                 {item}
                            //             </Typography>} />

                            //         </ListItem>
                            //     ))}

                            // </List>
        //                 </Grid>
                        

        //             ))}

        //         </Grid>

        //     </Container>
        // </Box>
    )
}

export default Skills
