import React from 'react'
import { Box, Container, Grid, Typography, Link, Button } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';
import LinkedInIcon from '@material-ui/icons/LinkedIn';

import Headshot from '../assets/headshot.jpg'

const useStyles = makeStyles({
    root: {
        marginTop: "15%",
    },
    headshot: {
        // margin: "2rem",
        width: "300px",
        borderRadius: "50%",
        border: "2px solid #000",
    },
    socialButton: {
        padding: "0",
        margin: "0",
        minWidth: "0",

        '&:hover': {
            color: "#fff",
            backgroundColor: "#000",
        },
    },
  });

const Banner = (props) => {
    const classes = useStyles(props);

    const openInNewTab = (url) => {
        window.open(url, "_blank")
      }

    return (
        <Box className={classes.root} align="center">
            <Container>
                <Grid container justify="center" spacing={2} md={12}>
                    <Grid item container direction="column" >
                        <Grid item>
                            <Typography variant="h1">
                                Vincent Good
                            </Typography>
                        </Grid>
                        <Grid item>
                        <Typography variant="h2">
                            Industrial Enginner
                        </Typography>
                        </Grid>
                        <Grid item>
                        <Button className={classes.socialButton} onClick={() => openInNewTab("https://www.linkedin.com/in/vincent-good")}>
                            <LinkedInIcon style={{ fontSize: 40 }} />
                        </Button>
                        </Grid>
                    </Grid>
                    <Grid item>
                        <img className={classes.headshot} src={Headshot} alt="headShot" />
                    </Grid>
                </Grid>
            </Container>
        </Box>
    )
}

export default Banner
