import React from 'react'
import { Box, Container, Grid, Typography, Link, Button } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';
import LinkedInIcon from '@material-ui/icons/LinkedIn';

import Headshot from '../assets/headshot.jpg'

const useStyles = makeStyles({
    root: {
        marginTop: "15%",
    },
    headshot: {
        margin: "2rem",
        width: "300px",
        borderRadius: "50%",
        border: "2px solid #000",
    },
    socialButton: {
        hover: {
            backgroundColor: "red",
        },
    },
  });

const Banner = (props) => {
    const classes = useStyles(props);

    const openInNewTab = (url) => {
        window.open(url, "_blank")
      }

    return (
        <Box className={classes.root} >
            <Container align="center">
                <Grid container justify="center">
                    <Grid item>
                        <Typography variant="h1">
                            Vincent Good
                        </Typography>
                        <Typography variant="h2">
                            Industrial Enginner
                        </Typography>
                        <Button className={classes.socialButton} hover onClick={() => openInNewTab("https://www.linkedin.com/in/vincent-good")}>
                            <LinkedInIcon style={{ fontSize: 40 }} />
                        </Button>
                    </Grid>
                    <Grid item>
                        <img className={classes.headshot} src={Headshot} alt="headShot" />
                    </Grid>
                </Grid>
            </Container>
        </Box>
    )
}

export default Banner
