import React from 'react'
import { Box, Container, Grid, Typography, Button } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';
import PhoneIcon from '@material-ui/icons/Phone';
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import Headshot from '../assets/headshot.jpg'
import { spacing } from '@material-ui/system';
import { theme } from '../theme';
const useStyles = makeStyles({
    root: {
        marginTop: "10%",
    },
    headshot: {
        width: "300px",
        borderRadius: "50%",
        border: "2px solid #000",
    },
    socialButton: {
        padding: "0",
        margin: "0",
        minWidth: "0",

        '&:hover': {
            color: "#fff",
            backgroundColor: "#000",
        },
    },
    socialIcon: {
        fontSize: 30,
    },
    // control: {
    //     padding: theme.spacing(4),
    // },
  });

const Banner = () => {
    const classes = useStyles();

    const openInNewTab = (url) => {
        window.open(url, "_blank")
      }

    return (
        <Box className={classes.root}>
            <Container>
                <Grid container justify="center" md>

                    <Grid item container direction="column" md={5}>

                        <Grid item>
                            <Typography variant="h1">
                                Vincent Good
                            </Typography>
                        </Grid>

                        <Grid item>
                            <Typography variant="h3">
                                Industrial Enginner
                            </Typography>
                        </Grid>

                        <Grid  containter direction="column" spacing={2}  >

                            <Grid item style={{backgroundColor:"red"}}>
                                <Button className={classes.socialButton} onClick={() => openInNewTab("https://www.linkedin.com/in/vincent-good")}>
                                    <LinkedInIcon className={classes.socialIcon} />
                                </Button>
                            </Grid>

                            <Grid item style={{backgroundColor:"red"}}>
                                <PhoneIcon className={classes.socialIcon} />
                            </Grid>

                        </Grid>

                    </Grid>

                    <Grid item align="center" md={4}>
                        <img className={classes.headshot} src={Headshot} alt="headShot" />
                    </Grid>

                </Grid>
            </Container>
        </Box>
    )
}

export default Banner
