import React from 'react'
import { darkTheme } from '../theme';
import { Box, Grid, Typography, Button, makeStyles, IconButton, Link } from '@material-ui/core'
import Headshot from '../assets/headshot.jpg'
import PhoneIcon from '@material-ui/icons/Phone';
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import CakeIcon from '@material-ui/icons/Cake';
import EmailIcon from '@material-ui/icons/Email';
import GetAppIcon from '@material-ui/icons/GetApp';

const socials = [
    {
        icon: PhoneIcon,
        info: "(+27)79 887 2395",
    },
    {
        icon: EmailIcon,
        info: "vincent.nick.good@gmail.com",
    },
    {
        icon: LocationOnIcon,
        info: "Pretoria, South Africa",
    },
    {
        icon: CakeIcon,
        info: "16 February 1997",
    },
]

const useStyles = makeStyles({
    headshot: {
        width: "250px",
        borderRadius: "50%",
    },

    socialButton: {
        padding: "0",
        margin: "0",
        minWidth: "0",

        '&:hover': {
            // color: "#fff555",
            borderRadius: "4px",
            
            backgroundColor: "#fff",
        },
    },
    socialIcon: {
        fontSize: 40,
        padding: "0.5rem",
        color: darkTheme.palette.primary.main,
    },
  });

const Banner = (props) => {
    const classes = useStyles();

    const openInNewTab = (url) => {
        window.open(url, "_blank")
      }

    return (
        // center align in mobile not wokring 
        // {{xs: "center", md: "left"}}
        // display="flex" flexDirection="column" alignItems="center"
            <Box className={props.className}>
                <Grid container spacing={10} alignItems="center" justifyContent="center">

                    <Grid item>
                        <Typography variant="h1">
                            Vincent Good
                        </Typography>

                        <Typography variant="h3">
                            Industrial Enginner
                        </Typography>

                        {socials.map((item, index) => (
                            <Box key={index} display="flex" alignItems="center">
                                {<item.icon className={classes.socialIcon} />}
                                <Typography variant="body1">
                                    {item.info}
                                </Typography>
                            </Box>
                        ))}

                        <Grid container alignItems="center" spacing={2}>

                            <Grid item>
                                <IconButton className={classes.socialButton} aria-label="test" onClick={() => openInNewTab("https://www.linkedin.com/in/vincent-good")}>
                                    <LinkedInIcon className={classes.socialIcon} style={{ fontSize: 63, color: "#0a66c2" }}/>
                                </IconButton>
                            </Grid>

                            <Grid item>
                                <Button
                                component={Link}
                                variant="contained"
                                color="primary"
                                startIcon={<GetAppIcon />}
                                download="VincentGood_cv.pdf"
                                href="/VincentGood_cv.pdf">
                                    <Typography variant="body1" >
                                        CV
                                    </Typography>
                                </Button>
                            </Grid>

                        </Grid>

                    </Grid>

                    <Grid item>
                        <img className={classes.headshot} src={Headshot} alt="headShot" />
                    </Grid>
                    
                </Grid>
            </Box>
    )
}

export default Banner
