import SectionColumns from "./common/SectionColumns";
import Card from "./common/Card";
import { Box, Typography, Grid, Container, List, ListItemIcon, ListItem, ListItemText, makeStyles, Paper } from "@material-ui/core"
import { darkTheme } from "../theme";
import LaptopMacIcon from '@material-ui/icons/LaptopMac';
import GroupIcon from '@material-ui/icons/Group';
import BeachAccessIcon from '@material-ui/icons/BeachAccess';
import FiberManualRecordIcon from '@material-ui/icons/FiberManualRecord';
import { BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const skills = [
    {
        title: "Technology",
        icon: <LaptopMacIcon viewBox="0 -7 24 24" style={{fontSize: "3rem" }}/>,
        info: [
            "Programming (Python, R)",
            "MySQL Database Service",
            "Power BI",
            "Microsoft Office suite",
            "Comfortable in Linux",
            "Anylogic: Simulation Modelling Software",
            ]
    },
    {
        title: "Leadership",
        icon: <GroupIcon />,
        info: [
            "Managed a restaurant alone regularly, in the absence of the manager",
            "House prefect at Pretoria Boys High School",
            "Tutored mathematics to school students",
            "Captain of the TuksUltimate Team",
            ]
    },
    {
        title: "Interests",
        icon: <BeachAccessIcon />,
        info: [
            "Technology – Across the board, from computers through materials and engineering",
            "Woodworking – Have made and sold home décor items",
            "Travel – Travelled to Europe, South East Asia and through Southern Africa",
            "Photography – Have done commercial assignments",
            ]
    },
]

const useStyles = makeStyles({
    socialIcon: {
        fontSize: 40,
        paddingTop: "0.5rem",
        paddingRight: "0.5rem",
        paddingLeft: 0,
        paddingBottom: "0.5rem",
        color: darkTheme.palette.primary.main,
    },
})

// const data = [
//     {name: "python", level: 0.85},
//     {name: "R Code", level: 0.5},
//     {name: "Random", level: 0.85},
// ]

const data = [
    {
      name: 'Page A',
      uv: 4000,
      pv: 2400,
      amt: 2400,
    },
    {
      name: 'Page B',
      uv: 3000,
      pv: 1398,
      amt: 2210,
    },
    {
      name: 'Page C',
      uv: 2000,
      pv: 9800,
      amt: 2290,
    },
    {
      name: 'Page D',
      uv: 2780,
      pv: 3908,
      amt: 2000,
    },
    {
      name: 'Page E',
      uv: 1890,
      pv: 4800,
      amt: 2181,
    },
    {
      name: 'Page F',
      uv: 2390,
      pv: 3800,
      amt: 2500,
    },
    {
      name: 'Page G',
      uv: 3490,
      pv: 4300,
      amt: 2100,
    },
  ];

const Skills = (props) => {
    const classes = useStyles()

    return (
        <SectionColumns title="Skills">

            {skills.map((item, index) => (
                <Card title={[item.icon, item.title]} md={3} key={index}>

    {/* <ResponsiveContainer width="100%" height="100%">
         <BarChart
          width={500}
          height={300}
          data={data}
          margin={{
            top: 5,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="pv" fill="#8884d8" />
          <Bar dataKey="uv" fill="#82ca9d" />
        </BarChart>
      </ResponsiveContainer> */}

                    {/* <ResponsiveContainer width="100%" height="100%">
                        <BarChart
                        width={500}
                        height={300}
                        data={data}
                        margin={{
                            top: 5,
                            right: 30,
                            left: 20,
                            bottom: 5,
                        }}>
                            <XAxis type="number" />
                            <YAxis type="category"/>
                            <Tooltip />
                            <Legend />
                            <Bar background label dataKey="level" fill="#8884d8" />
                        </BarChart>
                    </ResponsiveContainer> */}

                    <List dense={true}>

                    {item.info.map((skill, i) => (
                        <ListItem key={i}>

                            <ListItemIcon>
                                <FiberManualRecordIcon />
                            </ListItemIcon>

                            <ListItemText 
                            primary={
                            <Typography variant="body1">
                                {skill}
                            </Typography>} />

                        </ListItem>
                    ))}

                    </List>
                </Card>
            ))}

        </SectionColumns>
    )
}

export default Skills
