import React from 'react'
import { darkTheme } from '../theme';
import { Box, Container, Grid, Typography, Button, makeStyles } from '@material-ui/core'
// import Headshot from '../assets/headshot.jpg'
import PhoneIcon from '@material-ui/icons/Phone';
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import LocationOnIcon from '@material-ui/icons/LocationOn';
import CakeIcon from '@material-ui/icons/Cake';
import EmailIcon from '@material-ui/icons/Email';

const socials = [
    {
        icon: PhoneIcon,
        info: "(+27)79 887 2395",
    },
    {
        icon: EmailIcon,
        info: "vincent.nick.good@gmail.com",
    },
    {
        icon: LocationOnIcon,
        info: "Pretoria, South Africa",
    },
    {
        icon: CakeIcon,
        info: "16 February 1997",
    },
]

const useStyles = makeStyles({
    socialButton: {
        padding: "0",
        margin: "0",
        minWidth: "0",

        '&:hover': {
            color: "#fff",
            backgroundColor: "#000",
        },
    },
    socialIcon: {
        fontSize: 40,
        padding: "0.5rem",
        color: darkTheme.palette.primary.main,
    },
  });

const Banner = (props) => {
    const classes = useStyles();

    const openInNewTab = (url) => {
        window.open(url, "_blank")
      }

    return (
        // center align in mobile not wokring 
        // {{xs: "center", md: "left"}}
            <Box className={props.className} display="flex" flexDirection="column" alignItems="center">

                    <Typography variant="h1">
                        Vincent Good
                    </Typography>

                    <Typography variant="h3">
                        Industrial Enginner
                    </Typography>

                    {socials.map((item, index) => (
                        <Box key={index} display="flex" alignItems="center">
                            {<item.icon className={classes.socialIcon} />}
                            <Typography variant="body1">
                                {item.info}
                            </Typography>
                        </Box>
                    ))}

                    <Grid container alignItems="center">

                        <Grid item>
                            <Button className={classes.socialButton} onClick={() => openInNewTab("https://www.linkedin.com/in/vincent-good")}>
                                <LinkedInIcon className={classes.socialIcon} style={{ color: "#0a66c2" }}/>
                            </Button>
                        </Grid>

                        <Grid item>
                            <a download="your_cv.pdf" href="/static/resume.pdf" style={{ fontSize: 11 }}>
                                <Button variant="contained" color="primary">
                                    Download CV
                                </Button>
                            </a>
                        </Grid>
                    </Grid>

            </Box>
    )
}

export default Banner
